def dividUntilEnd(searParent,searChild, total):
    for i in range(len(searChild["mat"])):
        temp = findbyname(searChild["mat"][i])
        if len(temp) == 0:
            onlyonelist2(searChild, i, total, searParent)
        else : dividUntilEnd(searParent, temp[0], (searChild["num"][i]*total))

