def insertSear(searParent, mat, num):
    searParent["Mat"].append(mat)
    searParent["Num"].append(num)

def changeSear(searParent, num, i):
    searParent["Num"][i] += num


def onlyonelist2(searChild, partnum, total ,searParent):
    """중복검사 함수"""
    temp = onlyone(searChild["mat"][partnum], searParent["Mat"])
    multtemp = multInt(searChild["num"][partnum], total ,searChild["output"])
    if temp != -1:
        changeSear(searParent, multtemp, temp)
    else:
        insertSear(searParent, searChild["mat"][partnum], multtemp)

def onlyonelist(retemp, filtdum, dummy, dummyname):
    """중복검사 함수"""
    for i in range(len(retemp["mat"])):
        temp = onlyone(retemp["mat"][i], dummyname)
        if temp != -1:
            dummy[temp] += filtdum[i]
        else:
            dummyname.append(retemp["mat"][i])
            dummy.append(filtdum[i])

def onlyone(nam, dummyname):
    for i in range(len(dummyname)):
        if (nam == dummyname[i]):
            return i
    return -1


def multInputList(dummy, num):
    ans = []
    for i in dummy:
        ans.append(i*num)
    return ans

def multList(retemp,dum)->list:
    """list retemp["output"]와 dum을 곱해서 리스트로 반환하겠음"""
    dummy = []
    for i in retemp["num"]:
        dummy.append(i*dum/retemp["output"])  #하위재료 * 필요한 개수 / 하위재료로 상위재료를 만든 후 총 생산
    return dummy

def multInt(childnum, parentnum, childout)->list:
    """하위재료 * 필요한 개수 / 하위재료로 상위재료를 만든 후 총 생산"""
    return (childnum*parentnum/childout)  #하위재료 * 필요한 개수 / 하위재료로 상위재료를 만든 후 총 생산
  

def findbyname(findname:str)->list:   
    """전체 리스트에서 findname를 찾을 것임"""
    return list(filter(lambda m : m["name"] == findname, locaDataSear))


